$file = "capstone_static.vcxproj"
Copy-Item "..\capstone\msvc\capstone_static\$file" .
(Get-Content $file).replace('<RuntimeLibrary>MultiThreadedDebug<', '<RuntimeLibrary>MultiThreadedDebugDLL<').replace('<RuntimeLibrary>MultiThreaded<', '<RuntimeLibrary>MultiThreadedDLL<').replace('CAPSTONE_HAS_ARM;', '').replace('CAPSTONE_HAS_ARM64;', '').replace('CAPSTONE_HAS_MIPS;', '').replace('CAPSTONE_HAS_POWERPC;', '').replace('CAPSTONE_HAS_SPARC;', '').replace('CAPSTONE_HAS_SYSZ;', '').replace('CAPSTONE_HAS_XCORE;', '').replace('..\..\', '..\capstone\') | Select-String -Pattern '\\arch\\AArch64\\' -NotMatch | Select-String -Pattern '\\arch\\ARM\\' -NotMatch | Select-String -Pattern '\\arch\\Mips\\' -NotMatch | Select-String -Pattern '\\arch\\PowerPC\\' -NotMatch | Select-String -Pattern '\\arch\\Sparc\\' -NotMatch | Select-String -Pattern '\\arch\\SystemZ\\' -NotMatch | Select-String -Pattern '\\arch\\XCore\\' -NotMatch | Set-Content $file
